mapper.update:
update emp_info
set USER_NAME = #{userName,jdbcType=VARCHAR},
  SEX = #{sex,jdbcType=VARCHAR},
  CITY = #{city,jdbcType=VARCHAR},
  SIGN_NAME = #{signName,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}

mapper.select:
select ID, USER_NAME, SEX, CITY, SIGN_NAME
from emp_info
where ID = #{id,jdbcType=INTEGER}
